    <script src="https://unpkg.com/swiper/swiper-bundle.min.js"></script>
    <script>
        document.addEventListener('DOMContentLoaded', function () {

            var array = {!! json_encode($categoryRooms) !!};

            for (let i = 0; i < array.length ; i++) {
                new Swiper(`#slide_room_${i}`, {
                    loop: true, // Cho phép lặp lại các slide
                    navigation: {
                    nextEl: `#button-next-slide_${i}`,
                    prevEl: `#button-prev-slide_${i}`,
                    },
                    pagination: {
                    el: '.swiper-pagination',
                    clickable: true,
                    },
                    autoplay: false,
                });
            }

            $('.btn-nook_now').on('click', function(e) {
                let room_type = $(this).data('room_type');
                window.location.href = `/booking?room_type=${room_type}&step=1`;
            })
        });
    </script>
